let deferredPrompt;const installModal=new bootstrap.Modal(document.getElementById("installModal"));function installApp(){deferredPrompt.prompt(),deferredPrompt.userChoice.then((choiceResult=>{choiceResult.outcome,deferredPrompt=null,installModal.hide()}))}window.addEventListener("beforeinstallprompt",(event=>{event.preventDefault(),deferredPrompt=event,installModal.show();document.getElementById("installButton").addEventListener("click",(()=>{installApp()}))}));
